import * as React from 'react';
import { useState } from 'react';
import styles from './Calculator.module.css'

interface Props {
    streaming: {
        name: string;
        rate: number;
        count: number;
    };
    calculateTotal: () => void
    total: number;
}


export function Calculator(props: Props): React.ReactElement{

    const [count, setCount] = useState();

    function handleChange(value: string): void {
        if(isNaN(Number(value))) {
            alert("field can only be a number");
            return;
        }
        console.log("hello");
        setCount(value);
        props.streaming.count = Number(value);
        props.calculateTotal();
    }

    return(
        <div className={styles.container}>
            {props.streaming.name}
            <div className={styles.underline}>
                <input placeholder="30" className={styles.input} value={count} onChange={(event) => handleChange(event.target.value)}/>
            </div>
        </div>
    );
}
