import * as React from 'react';
import { useState } from 'react';
import styles from './Calculator.module.css'

interface Props {
    streaming: {
        name: string;
        rate: number;
        count: number;
    };
    calculateTotal: () => void
    total: number;
}


export default class Calculator<Props, State> extends React.PureComponent{

    public render(): React.ReactElement {

        const [count, setCount] = useState();

        function handleChange(value: string): void {
            if(isNaN(Number(value))) {
                alert("field can only be a number");
                if(count === undefined)
                    setCount('');
                else
                    setCount(count);
                return;
            }
            setCount(value);
            this.props.streaming.count = Number(value);
            this.props.calculateTotal();
        }



        return(
            <div className={styles.container}>
                {this.props.streaming.name}
                <div className={styles.arrowAndStreams}>
                    <div className={styles.arrow}/>
                    <div className={styles.underline}>
                        <input placeholder="N. of streams" className={styles.input} value={count} onChange={(event) => handleChange(event.target.value)}/>
                    </div>
                </div>
            </div>
        );
    }
}
