import { Result } from '../components/result/Result';
import { Calculator } from '../components/calculator/Calculator';
import React, { useState } from 'react';


import styles from './app.module.css'

interface Props {

}

interface State {
    total: number;
}

export default class Home extends React.PureComponent<Props, State> {

    constructor (props: Props) {
        super(props);
        this.state = { total: 0 };
    }

    public render(): React.ReactNode {

        const[streamings, setStreamings] = useState(streamingServices);

        const calculateTotal = () => {
            console.log(streamings[0].count);
            console.log('hello')
            let currentTotal = this.state.total;
            streamings.forEach(streaming => {
                currentTotal += streaming.count;
            }
            )
            this.setState({total: currentTotal})
        };

    return (
            <div className={styles.page}>
                <div className={styles.container}>
                    <div className={styles.streamingsContainer}>
                        {streamingServices.map(streaming => (
                                <Calculator streaming={streaming} calculateTotal={calculateTotal} total={this.state.total}/>
                        )
                        )}
                    </div>
                    <Result totalResult={this.state.total}/>
                </div>
            </div>
    );
 }

}


const streamingServices: {name: string, rate: number, count: number}[] = [
    {
        name: "Spotify",
        rate: 0.5,
        count: 0,
    },
    {
        name: "Apple Music",
