import { Result } from '../components/result/Result';
import { Calculator } from '../components/calculator/Calculator';
import React, { useState } from 'react';


import styles from './app.module.css'
import { number } from 'prop-types';
import { renderToHTML } from 'next/dist/next-server/server/render';

interface State {
    total: number;
}

export default class Home extends React.PureComponent<State> {

    constructor (props: Props) {
        super(props);
        this.state = { total: 0 };
    }

    public render(): React.ReactNode {

        const[streamings, setStreamings] = useState(streamingServices);

        function calculateTotal(): void {
            console.log(streamings[0].count);
            console.log('hello')
            const currentTotal = this.state.total;
            streamings.forEach(streaming => {
                this.setState({total: this.state.total + streaming.count})
            }
            )
        };

    return (
            <div className={styles.page}>
                <div className={styles.container}>
                    <div className={styles.streamingsContainer}>
                        {streamingServices.map(streaming => (
                                <Calculator streaming={streaming} calculateTotal={calculateTotal} total={total}/>
                        )
                        )}
                    </div>
                    <Result totalResult={this.state.total}/>
                </div>
            </div>
    );
 }

}


