import { Result } from '../components/result/Result';
import { Calculator } from '../components/calculator/Calculator';
import React, { useState } from 'react';


import styles from './app.module.css'

export const ServiceContext = React.createContext(null);
export default function Home() {


    const[streamings, setStreamings] = useState(streamingServices);
    const[total, setTotal] = useState(0);

    function calculateTotal(): void {
        if(total === undefined)
            setTotal(0);
        console.log(streamings[0].count);
        console.log(total + streamings[0].count);
        streamings.forEach(streaming => {
            console.log(total);
            setTotal(total + streaming.count)
        }
        )
    };

    return (
        <ServiceContext.Provider value={calculateTotal}>
            <div className={styles.page}>
                <div className={styles.container}>
                    <div className={styles.streamingsContainer}>
                        {streamingServices.map(streaming => 
                            <Calculator streaming={streaming} />
                        )}
                    </div>
                    <Result totalResult={total}/>
                </div>
            </div>
        </ServiceContext.Provider>
    );

}

const streamingServices: {name: string, rate: number, count: number}[] = [
    {
        name: "Spotify",
        rate: 0.5,
        count: 0,
    },
    {
        name: "Apple Music",
        rate: 0.5,
        count: 0,
    },
    {
        name: "Tidal",
        rate: 0.5,
        count: 0,
    },
    {
        name: "Deezer",
        rate: 0.5,
        count: 0,
    },

]
