import * as React from 'react';
import { useState } from 'react';
import styles from './Calculator.module.css'

interface Props {
    streaming: {
        name: string;
        rate: number;
        count: number;
    };
    calculateTotal: () => void
}


export function Calculator(props: Props): React.ReactElement{

    const [count, setCount] = useState();

    return(
        <div className={styles.container}>
            {props.streaming.name}
            <div className={styles.underline}>
                <input placeholder="30" className={styles.input} value={props.streaming.count} onChange={(value) => props.calculateTotal(event.target.value)}/>
            </div>
        </div>
    );
}
